<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.startup();if(isset($_GET['id'])){	// Извлечение статьи.	$article = article_get($_GET['id']);		// если в базе нет такой статьи	if(!is_array($article)){		header("HTTP/1.0 404 Not Found");		include(VIEW.'404.php');		exit;	}	}else{	header("HTTP/1.0 404 Not Found");	include(VIEW.'404.php');	exit;}// Подготовить внутренний шаблон страницы для передачи его в базовый шаблон$v_article = get_html( 'v_article.php', array('article' => $article) );// Подставить в базовый шаблон сайта, шаблон конкретной страницы// и вывести всё на экранecho get_html( 'v_base.php', array('content' => $v_article) );