<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Обработка отправки формы.if (!empty($_POST)){	// если всё получилось	if ( article_edit($_POST['id'], $_POST['title'], $_POST['content']) )	{		header('Location: editor.php');		die();	}		// запомнить введённые пользователем данные	// в случае ошибки	$article['id_article'] = $_POST['id'];	$article['title'] = $_POST['title'];	$article['content'] = $_POST['content'];		// флаг вывода ошибок	$error = true;}// Только пришли.elseif(isset($_GET['id'])){		// если хотим удалить	if(isset($_GET['delete']))	{		article_delete($_GET['id']);		header('Location: editor.php');		exit;	}	// вытащить статью	$article = article_get($_GET['id']);		// если в базе нет такой статьи	if(!is_array($article)){		header('Location: editor.php');		exit;	}	// флаг вывода ошибок	$error = false;}// Если ломятся без параметра.else{	header('Location: editor.php');	exit;}// Подготовить внутренний шаблон страницы для передачи его в базовый шаблон$v_edit = get_html( 	'v_edit.php', 	array('article' => $article, 'error' => $error)	);// Подставить в базовый шаблон сайта, шаблон конкретной страницы// и вывести всё на экранecho get_html( 'v_base.php', array('content' => $v_edit) );