<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Обработка отправки формы.if (!empty($_POST)){	// если новый пост сохранён	if ( article_new($_POST['title'], $_POST['content']) )	{		header('Location: editor.php');		die();	}		// запомнить введённые пользователем данные	// в случае ошибки	$title = $_POST['title'];	$content = $_POST['content'];		// флаг вывода ошибок	$error = true;}else{	$title = '';	$content = '';		// флаг вывода ошибок	$error = false;}// Подготовить внутренний шаблон страницы для передачи его в базовый шаблон$v_new = get_html( 	'v_new.php', 	array('title' => $title, 'content' => $content, 'error' => $error)	);// Подставить в базовый шаблон сайта, шаблон конкретной страницы// и вывести всё на экранecho get_html( 'v_base.php', array('content' => $v_new) );